mapper.update:
update mymember 
set mem_name=#{memName}, 
	mem_tel=#{memTel}, 
	mem_addr=#{memAddr} ,
	atch_file_id = #{atchFileId}
where mem_id=#{memId}

mapper.select:
select count(*) as cnt
from mymember
where mem_id = #{memId}

select mem_id as memid , mem_name as memname, mem_tel as memtel, mem_addr as memaddr, reg_dt as regdt, atch_file_id as atchFileId from mymember
and mem_id = #{memId}
and mem_name = #{memName}
and mem_tel = #{memTel}
and mem_addr like '%'|| #{memAddr} ||'%'
